gameActivityImportAdditions.insert:
import com.google.android.gms.analytics.GoogleAnalytics;
import com.google.android.gms.analytics.Tracker;
import com.google.android.gms.analytics.HitBuilders;
import com.google.android.gms.analytics.Logger.LogLevel;
import android.net.Uri;

gameActivityClassAdditions.insert:
  private String GoogleAnalyticsReferrer;
  private Tracker mTracker;

  String getReferrerFromUri(Uri uri) 
  {
	  if (uri == null) { return ""; }

	  return uri.toString();
  }

  synchronized public void AndroidThunkJava_GoogleAnalyticsStartSession(String TrackingId, int DispatchInterval)
  {
	  try 
	  {
		  if (mTracker == null) 
		  {
			  GoogleAnalytics analytics = GoogleAnalytics.getInstance(this);
			  if(analytics != null) 
			  {
				  if(DispatchInterval > 0) 
				  {
					analytics.setLocalDispatchPeriod(DispatchInterval);
				  }
				  mTracker = analytics.newTracker(TrackingId);
				  mTracker.enableAdvertisingIdCollection(true);
				  mTracker.enableExceptionReporting(true);
				  mTracker.setSessionTimeout(300);
			  }
		  }

		  if(mTracker != null) 
		  {
			  mTracker.setScreenName("Game Launched");
			  mTracker.send(new HitBuilders.ScreenViewBuilder()
				  .setCampaignParamsFromUrl(GoogleAnalyticsReferrer)
				  .build());
		  }
	  } 
	  catch(Exception e) 
	  {
		e.printStackTrace();
	  }
  }

  public void AndroidThunkJava_GoogleAnalyticsRecordScreen(String ScreenName)
  {
	  try 
	  {
		  if(mTracker != null) 
		  {
			  mTracker.setScreenName(ScreenName);
			  mTracker.send(new HitBuilders.ScreenViewBuilder().build());
		  }
	  } 
	  catch(Exception e) 
	  {
		  e.printStackTrace();
	  }
  }

  public void AndroidThunkJava_GoogleAnalyticsRecordEvent(String Category, String Action, String Label, int Value)
  {
	  try 
	  {
		  if(mTracker != null) 
		  {
			  HitBuilders.EventBuilder eventBuilder = new HitBuilders.EventBuilder();
			  eventBuilder.setCategory(Category).setAction(Action);
			  eventBuilder.setValue(Value);

			  if (!Label.equals(""))
			  {
				eventBuilder.setLabel(Label);
			  }

			  mTracker.send(eventBuilder.build());
		  }
	  } 
	  catch(Exception e) 
	  {
		  e.printStackTrace();
	  }
  }

  public void AndroidThunkJava_GoogleAnalyticsFlushEvents()
  {
	  try 
	  {
		  if(mTracker != null) 
		  {
			  GoogleAnalytics.getInstance(getBaseContext()).dispatchLocalHits();
		  }
	  } 
	  catch(Exception e) 
	  {
		  e.printStackTrace();
	  }
  }

  public void AndroidThunkJava_GoogleAnalyticsSetUserId(String UserId)
  {
	  try 
	  {
		  if(mTracker != null) 
		  {
			  mTracker.set("&uid", UserId);
		  }
	  } 
	  catch(Exception e) 
	  {
		  e.printStackTrace();
	  }
  }

  public String AndroidThunkJava_GoogleAnalyticsGetUserId()
  {
	  try 
	  {
		  if(mTracker != null) 
		  {
			  return mTracker.get("&uid");
		  }
	  } 
	  catch(Exception e) 
	  {
		  e.printStackTrace();
	  }
	  return "";
  }

  public void AndroidThunkJava_GoogleAnalyticsSetLocation(String Location)
  {
	  try 
	  {
		  if(mTracker != null) 
		  {
			  mTracker.set("&geoid", Location);
		  }
	  } 
	  catch(Exception e) 
	  {
		  e.printStackTrace();
	  }
  }

  public void AndroidThunkJava_GoogleAnalyticsRecordError(String Description)
  {
	  try 
	  {
		  if(mTracker != null) 
		  {
			  mTracker.send(new HitBuilders.ExceptionBuilder()
			  .setDescription(Description)
			  .setFatal(false)
			  .build());
		  }
	  } 
	  catch(Exception e) 
	  {
		  e.printStackTrace();
	  }
  }

  public void AndroidThunkJava_GoogleAnalyticsRecordCurrencyPurchase(String TransactionId, String GameCurrencyType, int GameCurrencyAmount, String RealCurrencyType, float RealMoneyCost, String PaymentProvider)
  {
	  try 
	  {
		  if(mTracker != null) 
		  {
			  mTracker.send(new HitBuilders.TransactionBuilder()
					  .setTransactionId(TransactionId)
					  .setAffiliation(PaymentProvider)
					  .setRevenue(RealMoneyCost)
					  .setTax(0)
					  .setShipping(0)
					  .setCurrencyCode(RealCurrencyType)
					  .build());

			  mTracker.send(new HitBuilders.ItemBuilder()
					  .setTransactionId(TransactionId)
					  .setName(GameCurrencyType)
					  .setSku(GameCurrencyType)
					  .setCategory(PaymentProvider)
					  .setPrice((RealMoneyCost / GameCurrencyAmount))
					  .setQuantity(GameCurrencyAmount)
					  .setCurrencyCode(RealCurrencyType)
					  .build());
		  }
	  } 
	  catch(Exception e) 
	  {
		  e.printStackTrace();
	  }
  }

  public void AndroidThunkJava_GoogleAnalyticsRecordSocialInteraction(String Network, String Action, String Target)
  {
	  try 
	  {
		  if(mTracker != null) 
		  {
			  HitBuilders.SocialBuilder socialBuilder = new HitBuilders.SocialBuilder();
			  socialBuilder.setNetwork(Network);
			  socialBuilder.setAction(Action);

			  if (!Target.equals(""))
			  {
				  socialBuilder.setTarget(Target);
			  }

			  mTracker.send(socialBuilder.build());
		  }
	  } 
	  catch(Exception e) 
	  {
		  e.printStackTrace();
	  }
  }

  public void AndroidThunkJava_GoogleAnalyticsRecordUserTiming(String Category, int Value, String Name)
  {
	  try 
	  {
		  if(mTracker != null) 
		  {
			  HitBuilders.TimingBuilder timingBuilder = new HitBuilders.TimingBuilder();
			  timingBuilder.setCategory(Category);
			  timingBuilder.setValue(Value);
			  timingBuilder.setVariable(Name);

			  mTracker.send(timingBuilder.build());
		  }
	  } 
	  catch(Exception e) 
	  {
		  e.printStackTrace();
	  }
  }

